import { Component } from "react";
import { nanoid } from 'nanoid'

import styles from './phoneBook.module.css'

class PhoneBook extends Component {

state = {
    contacts: [
        {id: 'id-1', name: 'Rosie Simpson', number: '459-12-56'},
        {id: 'id-2', name: 'Hermione Kline', number: '443-89-12'},
        {id: 'id-3', name: 'Eden Clements', number: '645-17-79'},
        {id: 'id-4', name: 'Annie Copeland', number: '227-91-26'},
    ],
    filter: '',
    name: '',
    number: ''
}

    addContact = (event) => {
        event.preventDefault();
        this.setState(prevState => {
            const { contacts, name, number } = prevState;
            const newContact = {
                name,
                number,
                id: nanoid()
            }
            return {
                contacts: [...contacts, newContact]
            }
        })
    }

    deleteContact = (id) => {
        this.setState(prevState => {
            const { contacts } = prevState;
            return {
                contacts:  contacts.filter(contact => contact.id !== id)
            }
        })
    }

    handleChange = ({target}) => {
        const { name, value } = target;
        this.setState({
             [name]: value
        })
    } 

    changeFilter = ({ target }) => {
        this.setState({
            filter: target.value
        })
    }

    getFilteredContacts() {
        const { filter, contacts } = this.state;
        if (!filter) {
            return contacts 
        }
        const filterText = filter.toLowerCase();
        const filterContacts = contacts.filter(({ name }) => {
            const result = name.toLowerCase().includes(filterText);
            return result
        })
        return filterContacts;
    }


    render() {
        const {  filter, name, number } = this.state;
        const { handleChange, addContact, deleteContact, changeFilter } = this;

        const contacts = this.getFilteredContacts();

        const elements = contacts.map(({ id, name, number }) => (
            <li key={id} > {name}: {number} <button onClick={() => deleteContact(id)}>Delete</button> </li>
        ));
        return (
            <div>
                <form action="" onSubmit={addContact}>
                    <div className={styles.namebox}>
                    <label htmlFor="">Name</label>
                        <input
                            value={name}
                        type="text"
                        name="name"
                        onChange={handleChange}
                        pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
                        title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
                        required

                    />
                    </div>
                    <div className={styles.numberbox}>
                        <label htmlFor="">Number</label>
                        <input
                            value={number}
                            type="tel"
                            name="number"
                            onChange={handleChange}
                            pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
                            title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
                            required
                        />
                    </div>
                    <button type="submit" >Add contact</button>
                    <div>
                        <input
                            onChange={changeFilter}
                            value={filter}
                            type="text"
                            name="filter"/>
                        <ul>{elements}</ul>
                    </div>
                </form>
            </div>
        )
    }
}

export default PhoneBook
