import { Component } from "react";
import { nanoid } from 'nanoid'

import styles from './phoneBook.module.css'

class PhoneBook extends Component {

state = {
  contacts: [],
    name: '',
  number: ''
}

    addContact = (event) => {
        event.preventDefault();
        this.setState(prevState => {
            const { contacts, name, number } = prevState;
            const newContact = {
                name,
                number,
                id: nanoid()
            }
            return {
               contacts: [...contacts, newContact]
            }
        })
    }

    deleteContact = (id) => {
        this.setState(prevState => {
            const { items } = prevState;
            return {
                items: items.filter(item => item.id !== id)
            }
        })
    }

    handleChange = ({target}) => {
        const { name, value } = target;
        this.setState({[name]: value })
    } 

    render() {
        const { contacts, name, number } = this.state;
        const { handleChange, addContact, deleteContact  } = this;
        const elements = contacts.map(({ id, name, number }) => (
            <li key={id} > {name}: {number} <button>Delete</button> </li>
        ));
        return (
            <div>
                <form action="" onSubmit={addContact}>
                    <div className={styles.namebox}>
                    <label htmlFor="">Name</label>
                        <input
                            value={name}
                        type="text"
                        name="name"
                        onChange={handleChange}
                        pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
                        title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
                        required

                    />
                    </div>
                    <div className={styles.numberbox}>
                        <label htmlFor="">Number</label>
                        <input
                            value={number}
                            type="tel"
                            name="number"
                            onChange={handleChange}
                            pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
                            title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
                            required
                        />
                    </div>
                    <button type="submit" onClick={deleteContact}>Add contact</button>
                    <div><ul>{ elements }</ul></div>
                </form>
            </div>
        )
    }
}

export default PhoneBook
