import { Component } from "react";
import { nanoid } from 'nanoid'

import ContactForm from "./ContactForm";

// import styles from './phoneBook.module.css'

class PhoneBook extends Component {

state = {
    contacts: [
        {id: 'id-1', name: 'Rosie Simpson', number: '459-12-56'},
        {id: 'id-2', name: 'Hermione Kline', number: '443-89-12'},
        {id: 'id-3', name: 'Eden Clements', number: '645-17-79'},
        {id: 'id-4', name: 'Annie Copeland', number: '227-91-26'},
    ],
    filter: '',
}

    addContact = (data) => {
        const { contacts } = this.state;
            const dublicate = contacts.find(contact => contact.name === data.name);
            if (dublicate) {
                alert(`${data.name} is already in contacts`);
                return
            }
        this.setState(prevState => {
            const { contacts } = prevState;
            const { name, number } = data;
            const newContact = {
                name,
                number,
                id: nanoid() 
            }
            return {
                contacts: [...contacts, newContact]
            }
        })
    }

    deleteContact = (id) => {
        this.setState(prevState => {
            const { contacts } = prevState;
            return {
                contacts:  contacts.filter(contact => contact.id !== id)
            }
        })
    }


    changeFilter = ({ target }) => {
        this.setState({
            filter: target.value
        })
    }

    getFilteredContacts() {
        const { filter, contacts } = this.state;
        if (!filter) {
            return contacts 
        }
        const filterText = filter.toLowerCase();
        const filterContacts = contacts.filter(({ name }) => {
            const result = name.toLowerCase().includes(filterText);
            return result
        })
        return filterContacts;
    }


    render() {
        const {  filter, name, number } = this.state;
        const { handleChange, addContact, deleteContact, changeFilter } = this;

        const contacts = this.getFilteredContacts();

        const elements = contacts.map(({ id, name, number }) => (
            <li key={id} > {name}: {number} <button onClick={() => deleteContact(id)}>Delete</button> </li>
        ));
        return (
            <div>
               <ContactForm onSubmit={addContact} />
                    <div>
                        <input
                            onChange={changeFilter}
                            value={filter}
                            type="text"
                            name="filter"/>
                        <ul>{elements}</ul>
                    </div>

            </div>
        )
    }
}

export default PhoneBook;
